const input = $input.first().json.body || $input.first().json;

const historyContext = (input.history || [])
  .slice(-4)
  .map(h => `${h.role}: ${h.content}`)
  .join('\n');

return {
  json: {
    query: input.query || input.message || '',
    conversation_id: input.conversation_id || 'default',
    history_context: historyContext
  }
};

const input = $('Prepare Query').first().json;
const expansion = $input.first().json;

let queries = [input.query];

try {
  let content = expansion.message?.content || expansion.content || expansion.text;
  if (typeof content === 'string') {
    content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
    const expanded = JSON.parse(content);
    if (Array.isArray(expanded)) {
      queries = [...queries, ...expanded.slice(0, 2)];
    }
  }
} catch (e) {
  // Keep just original query
}

return {
  json: {
    ...input,
    search_queries: queries
  }
};

const input = $('Parse Queries').first().json;
const results = $input.all();

if (!results || results.length === 0) {
  return {
    json: {
      ...input,
      context: "No relevant documents found in the knowledge base.",
      sources: [],
      has_context: false
    }
  };
}

const contextParts = results.map((r, i) => {
  const doc = r.json;
  return `
[Source ${i + 1}]
Document: ${doc.act_name || 'Income Tax Documentation'}
Section: ${doc.section_number || 'N/A'} - ${doc.section_title || 'N/A'}
Location: ${doc.breadcrumb || 'N/A'}
Relevance: ${((doc.similarity || 0) * 100).toFixed(1)}%

Content:
${doc.content || ''}

Summary:
${doc.summary || 'No summary available'}
---`;
}).join('\n');

const sources = results.map(r => ({
  document: r.json.act_name || 'Income Tax Documentation',
  section: `Section ${r.json.section_number || 'N/A'}`,
  section_title: r.json.section_title || '',
  breadcrumb: r.json.breadcrumb || '',
  excerpt: (r.json.content || '').substring(0, 200) + '...',
  similarity: r.json.similarity || 0
}));

return {
  json: {
    ...input,
    context: contextParts,
    sources: sources,
    has_context: true
  }
};

