parameters.jsCode:
const context = $('Format Context').first().json;
const answer = $input.first().json;
const answerText = answer.message?.content || answer.content || answer.text || '';

const domain = 'income_tax';

// Extract cited sources
const citedSources = [];
const sources = context.sources || [];

for (const source of sources) {
  if (answerText.includes(source.section) || 
      answerText.includes(source.section_title) ||
      answerText.includes(source.breadcrumb)) {
    citedSources.push({
      document: source.document,
      section: source.section,
      breadcrumb: source.breadcrumb,
      excerpt: source.excerpt
    });
  }
}

// Include top sources if none explicitly cited
if (citedSources.length === 0 && sources.length > 0) {
  citedSources.push(...sources.slice(0, 2).map(s => ({
    document: s.document,
    section: s.section,
    breadcrumb: s.breadcrumb,
    excerpt: s.excerpt
  })));
}

// Income Tax-specific follow-up suggestions
const followups = [
  "How do I calculate PAYE?",
  "What deductions can I claim?",
  "When is my tax return due?"
];

return {
  json: {
    success: true,
    response: {
      text: answerText,
      citations: citedSources,
      domain: domain,
      confidence: context.has_context ? Math.max(...sources.map(s => s.similarity || 0.5)) : 0.3,
      suggested_followups: followups
    }
  }
};
